import React, {Component} from 'react'; 

class Welcome extends Component {
  constructor(){
    super(); 
    this.state = {
      count : 0,
    }
  }

  addOne(){
    this.setState({
      count: this.state
    })
  }

  render(){
    return(
      <div>
        <h1>Welcome {this.props.name}</h1>
        <p>Mon compteur : {this.state.count}</p>
        <button>Ajouter 1</button>
      </div>
    )
  }
}

export default Welcome;
